import React, { useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import Menubartemplate from "./template/Menubartemplate.js";
import { Button } from "primereact/button";
import { InputNumber } from "primereact/inputnumber";
import { Card } from "primereact/card";

function App() {
  const [data, setData] = useState(null);
  const [fibonacci, setFibonacci] = useState([]);
  const [number, setNumber] = useState();

  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));
    fetch("/fibonacci")
      .then((res) => res.json())
      .then((data) => {
        setFibonacci(data);
      });
  }, []);

  return (
    <div className="App">
      <Menubartemplate />

      <div className="form-demo">
        <Dialog
          visible={showMessage}
          onHide={() => setShowMessage(false)}
          position="top"
          footer={dialogFooter}
          showHeader={false}
          breakpoints={{ "960px": "80vw" }}
          style={{ width: "30vw" }}
        >
          <div className="p-d-flex p-ai-center p-dir-col p-pt-6 p-px-3">
            <i
              className="pi pi-check-circle"
              style={{ fontSize: "5rem", color: "var(--green-500)" }}
            ></i>
            <h5>Registration Successful!</h5>
            <p style={{ lineHeight: 1.5, textIndent: "1rem" }}>
              Your account is registered under name <b>{formData.name}</b> ;
              it'll be valid next 30 days without activation. Please check{" "}
              <b>{formData.email}</b> for activation instructions.
            </p>
          </div>
        </Dialog>

        <div className="p-d-flex p-jc-center">
          <div className="card">
            <h5 className="p-text-center">Register</h5>
            <Form
              onSubmit={onSubmit}
              initialValues={{
                name: "",
                email: "",
                password: "",
                date: null,
                country: null,
                accept: false,
              }}
              validate={validate}
              render={({ handleSubmit }) => (
                <form onSubmit={handleSubmit} className="p-fluid">
                  <Field
                    name="name"
                    render={({ input, meta }) => (
                      <div className="p-field">
                        <span className="p-float-label">
                          <InputText
                            id="name"
                            {...input}
                            autoFocus
                            className={classNames({
                              "p-invalid": isFormFieldValid(meta),
                            })}
                          />
                          <label
                            htmlFor="name"
                            className={classNames({
                              "p-error": isFormFieldValid(meta),
                            })}
                          >
                            Name*
                          </label>
                        </span>
                        {getFormErrorMessage(meta)}
                      </div>
                    )}
                  />
                  <Field
                    name="email"
                    render={({ input, meta }) => (
                      <div className="p-field">
                        <span className="p-float-label p-input-icon-right">
                          <i className="pi pi-envelope" />
                          <InputText
                            id="email"
                            {...input}
                            className={classNames({
                              "p-invalid": isFormFieldValid(meta),
                            })}
                          />
                          <label
                            htmlFor="email"
                            className={classNames({
                              "p-error": isFormFieldValid(meta),
                            })}
                          >
                            Email*
                          </label>
                        </span>
                        {getFormErrorMessage(meta)}
                      </div>
                    )}
                  />
                  <Field
                    name="password"
                    render={({ input, meta }) => (
                      <div className="p-field">
                        <span className="p-float-label">
                          <Password
                            id="password"
                            {...input}
                            toggleMask
                            className={classNames({
                              "p-invalid": isFormFieldValid(meta),
                            })}
                            header={passwordHeader}
                            footer={passwordFooter}
                          />
                          <label
                            htmlFor="password"
                            className={classNames({
                              "p-error": isFormFieldValid(meta),
                            })}
                          >
                            Password*
                          </label>
                        </span>
                        {getFormErrorMessage(meta)}
                      </div>
                    )}
                  />
                  <Field
                    name="date"
                    render={({ input }) => (
                      <div className="p-field">
                        <span className="p-float-label">
                          <Calendar
                            id="date"
                            {...input}
                            dateFormat="dd/mm/yy"
                            mask="99/99/9999"
                            showIcon
                          />
                          <label htmlFor="date">Birthday</label>
                        </span>
                      </div>
                    )}
                  />
                  <Field
                    name="country"
                    render={({ input }) => (
                      <div className="p-field">
                        <span className="p-float-label">
                          <Dropdown
                            id="country"
                            {...input}
                            options={countries}
                            optionLabel="name"
                          />
                          <label htmlFor="country">Country</label>
                        </span>
                      </div>
                    )}
                  />
                  <Field
                    name="accept"
                    type="checkbox"
                    render={({ input, meta }) => (
                      <div className="p-field-checkbox">
                        <Checkbox
                          inputId="accept"
                          {...input}
                          className={classNames({
                            "p-invalid": isFormFieldValid(meta),
                          })}
                        />
                        <label
                          htmlFor="accept"
                          className={classNames({
                            "p-error": isFormFieldValid(meta),
                          })}
                        >
                          I agree to the terms and conditions*
                        </label>
                      </div>
                    )}
                  />

                  <Button type="submit" label="Submit" className="p-mt-2" />
                </form>
              )}
            />
          </div>
        </div>
      </div>

      <div className="p-formgroup-inline">
        <div className="p-field">
          <label htmlFor="firstname5" className="p-sr-only">
            Firstname
          </label>
          <InputNumber
            inputId="integeronly"
            value={number}
            onValueChange={(e) => setNumber(e.value)}
          />
        </div>
        <Button type="button" label="Submit" />
      </div>
    </div>
  );
}

export default App;
